import urllib.request
import http.client
import datetime
import json


def getAddress(place):
  request = urllib.request.Request(
    f"https://nominatim.openstreetmap.org/search?q={place.replace(' ', '+')}&format=jsonv2&addressdetails=1&limit=1",
    headers={
      "User-Agent": "ScreenDeck/Weather App"
    }
  )

  with urllib.request.urlopen(request) as response:
    data = response.read().decode('utf-8')

  return json.loads(data)


def get_weather_data(latitude, longitude):
  conn = http.client.HTTPSConnection("api.open-meteo.com")
  endpoint = f"/v1/forecast?latitude={latitude}&longitude={longitude}&hourly=temperature_2m,relative_humidity_2m,precipitation_probability,weather_code,wind_speed_10m&forecast_days=1"
  conn.request("GET", endpoint)
  res = conn.getresponse()
  data = res.read().decode('utf-8')
  weather_data = json.loads(data)
  return weather_data


