from html import appWidget, homePage, settingsWrapper, mainSettingsWrapper, enterPassword
from flask import Flask, request, jsonify
from markupsafe import escape
from notifypy import Notify
from waitress import serve
import inspect
import base64
import socket
import json
import os


BUILD_VERSION = [1, 0, 0]
try:
	PASSWORD = json.load(open("settings.json"))["ScreenDeck"]["password"]
except FileNotFoundError:
	with open('settings.json', 'w') as file:
			json.dump({
				"ScreenDeck": {
					"password": "0000",
					"ip": "192.168.1.124",
					"port": "5000"
				}
			}, file) 

app = Flask(__name__)

sdApps = []
currentParsingApp: str = None

currentActiveApp: str = None

@app.route("/")
def home():
	if request.args.get("password") == PASSWORD:
		html = ""
		for spApp in sdApps:
			html += appWidget(spApp["name"], spApp["id"], spApp["icon"])
		html += appWidget(
			"Settings",
			"0",
			"data:image/png;base64,",
		)

		return homePage(html)
	else:
		return enterPassword()


@app.route("/saveSettings", methods=["POST"])
def saveSettings():
	if request.args.get("password") == PASSWORD:
		with open("settings.json", "r") as F:
			existing_settings = json.load(F)

		new_settings = request.get_json()

		for appId, appSettings in new_settings.items():
			if appId not in existing_settings:
				existing_settings[appId] = {}
			for setting, value in appSettings.items():
				existing_settings[appId][setting] = value

		with open("settings.json", "w") as F:
			json.dump(existing_settings, F)

	return ""


@app.route("/close", methods=["POST"])
def close():
	try:
		res = eval("close_" + currentActiveApp + "()")
		if res is not None:
			return res
		else:
			return ""
	except:
		return ""

@app.route("/load", methods=["POST"])
def load():
	if request.args.get("password") == PASSWORD:
		appId = request.get_json()["id"]

		global currentActiveApp
		currentActiveApp = appId
		
		if appId == "0":
			jsonData = json.load(open("./settings.json", "r"))

			password = jsonData["ScreenDeck"]["password"]
			ip = jsonData["ScreenDeck"]["ip"]
			port = jsonData["ScreenDeck"]["port"]

			appSettings = settingsWrapper(
				"ScreenDeck",
				f"""
		<div style="display: flex; flex-direction: row; align-items: center; justify-content: space-between; padding-bottom: 6px;">
		  <p style="color: white; margin: 0;">Password</p>
		  <input type="text" value="{password}" id="ScreenDeck￾password"">
		</div>
		<div style="display: flex; flex-direction: row; align-items: center; justify-content: space-between; padding-bottom: 6px;">
		  <p style="color: white; margin: 0;">IP</p>
		  <input type="text" value="{ip}" id="ScreenDeck￾ip"">
		</div>
		<div style="display: flex; flex-direction: row; align-items: center; justify-content: space-between; padding-bottom: 6px;">
		  <p style="color: white; margin: 0;">Port</p>
		  <input type="text" value="{port}" id="ScreenDeck￾port"">
		</div>
		""",
			)

			for spApp in sdApps:
				settings = ""
				try:
					for setting in spApp["settings"].split("￾"):
						name, typeId, value = setting.split("￢")
						if spApp["id"] in jsonData:
							if name in jsonData[spApp["id"]]:
								value = jsonData[spApp["id"]][name]
						if typeId == "text" or typeId == "number":
							if not name.startswith("!"):
								settings += f"""<div style="display: flex; flex-direction: row; align-items: center; justify-content: space-between; padding-bottom: 6px;">
								   <p style="color: white; margin: 0;">{name}</p>
								   <input style="" type="{typeId}" value="{escape(value)}" id="{spApp['id']}￾{name}">
								 </div>"""
						elif typeId == "bool":
							if not name.startswith("!"):
								settings += f"""<div style="display: flex; flex-direction: row; align-items: center; padding-bottom: 6px; gap: 10px;">
									<p style="color: white; margin: 0;">{name}</p>
									<div class="checkbox-wrapper-3">
									<input type="checkbox" id="{spApp['id']}￾{name}" {'checked' if value == True else ''}/>
									<label for="{spApp['id']}￾{name}" class="toggle"><span></span></label>
									</div>
								</div>"""

					appSettings += settingsWrapper(spApp["name"], settings)
				except ValueError:
					pass

			return jsonify({"fs": str(False), "html": mainSettingsWrapper(appSettings)})

		return jsonify(
			{
				"fs": str([x for x in sdApps if x["id"] == appId][0]["fs"] == "￢"),
				"html": eval("load_" + appId + "()"),
			}
		)
	return ""


def getSetting(setting, appId = None):
	if appId == None:
		appId = inspect.stack()[1].function[5:]
	settings = json.load(open("settings.json"))

	if appId in settings:
		if setting in settings[appId]:
			try:
				return int(settings[appId][setting])
			except ValueError:
				return settings[appId][setting]

	try:
		for s in [x for x in sdApps if x["id"] == appId][0]["settings"].split("￾"):
			name, _, value = s.split("￢")
			if name == setting:
				try:
					return int(value)
				except ValueError:
					return value
	except IndexError:
		try:
			return settings[appId][setting]
		except:
			with open(f"./apps/{currentParsingApp}.sdapp", "r", encoding="utf-8") as f:
				content = f.read().split("�")

				appData = {}
				for i in content[:-1]:
					a, b = i.split(" = ")
					appData[a] = b
				
				for s in appData["settings"].split("￾"):
					name, _, value = s.split("￢")
					if name == setting:
						try:
							return int(value)
						except ValueError:
							return value
	return 0


def display_notification():
	hostname = socket.gethostname()
	ip_address = socket.gethostbyname(hostname)
	
	if getSetting("ip", "ScreenDeck") == "":
		with open('settings.json', 'r') as file:
			data = json.load(file)
			data['ScreenDeck']['ip'] = ip_address
			with open('settings.json', 'w') as file:
				json.dump(data, file)

	notification = Notify()
	notification.title = "[ScreenDeck] Server is running"
	notification.message = f"Running on {ip_address}:{getSetting("port", "ScreenDeck")}"

	notification.send()


def compare_versions(version1, version2):
	for v1, v2 in zip(version1, version2):
		return v1 <= v2

	return len(version1) <= len(version2)


for file in os.listdir("./apps"):
	if file.endswith(".sdapp"):
		with open("./apps/" + file, "r", encoding="utf-8") as f:
			content = f.read().split("�")

			appData = {}
			for i in content[:-1]:
				a, b = i.split(" = ")
				appData[a] = b

			version_list = [int(part) for part in appData["minVersion"].split(".")]
			if compare_versions(version_list, BUILD_VERSION):
				currentParsingApp = appData["name"]
				try:
					code = base64.b64decode(content[-1]).decode("utf-8")
				except:
					code = content[-1]
				exec(
					code
						.replace("\\n", "\n")
						.replace("def load", "def load_" + appData["id"])
						.replace("def close", "def close_" + appData["id"])
				)
				sdApps.append(appData)


display_notification()
serve(app, host="0.0.0.0", port=getSetting("port", "ScreenDeck"))
